#' Correlation
#'
#' 
#' @param numer .
#' @param korel .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$izpis} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$scat} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$izpis$asDF}
#'
#' \code{as.data.frame(results$izpis)}
#'
#' @export
simr <- function(
    numer = 50,
    korel = 0.7) {

    if ( ! requireNamespace('jmvcore'))
        stop('simr requires jmvcore to be installed (restart may be required)')


    options <- simrOptions$new(
        numer = numer,
        korel = korel)

    analysis <- simrClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}
